
public class Ex09_for문_별짓기3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		for (int k = 1; k <= 5 ; k++) {
			
			for (int i = 1; i <= 5-k ; i++) {
				System.out.print(" ");
			}
			for(int i = 1 ; i <= k ; i++) {
				System.out.print("*");
			}
			System.out.println();
				
				
				
				
				
		}
		
		
	}

}
